package com.example.api.Controllers;import com.example.api.DTO.CommentDTO;import com.example.api.Entity.Comment;import com.example.api.Response.ApiResponse;import com.example.api.Services.CommentService;import com.example.api.ServicesImpl.CommentServiceImplementation;import jakarta.servlet.http.HttpSession;import lombok.RequiredArgsConstructor;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;@RestController@RequiredArgsConstructor@RequestMapping("api/v1/comment")public class CommentController {    private final CommentServiceImplementation commentService;    @PostMapping("/add/{postId}")    public ResponseEntity<ApiResponse> createOpen(@PathVariable Long postId,                                                  @RequestBody CommentDTO commentDTO,                                                  HttpSession session){        CommentDTO comment = commentService.commentCreated(commentDTO,postId,session);        return  ResponseEntity.status(HttpStatus.CREATED).body(                ApiResponse.builder().                        data(new Object())                        .message((comment!=null)?"Success" : "Error").                        status((comment!=null)?HttpStatus.CREATED:HttpStatus.EXPECTATION_FAILED).                build()        );    }}