package com.example.api.Controllers;import com.example.api.DTO.CommentDTO;import com.example.api.DTO.LikeRequestDto;import com.example.api.Entity.Likes;import com.example.api.Response.ApiResponse;import com.example.api.ServicesImpl.LikeServiceImplementation;import jakarta.servlet.http.HttpSession;import lombok.RequiredArgsConstructor;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;@RestController@RequestMapping("api/v1/like")@RequiredArgsConstructorpublic class LikePostController {    private final LikeServiceImplementation likeServiceImplementation;    @PostMapping("/add/{postId}")    public ResponseEntity<ApiResponse> Like(@PathVariable Long postId,                                                  @RequestBody LikeRequestDto like,                                                  HttpSession session){        LikeRequestDto likes = likeServiceImplementation.likeOrUnlikePost(postId,like,session);          return ResponseEntity.status(HttpStatus.CREATED).body(                ApiResponse.builder()                        .data(likes)                        .message((likes!=null) ? "SUCCESS" : "ERROR")                        .status(HttpStatus.CREATED)                        .build()        );    }}