package com.example.api.Controllers;import com.example.api.DTO.LikeRequestDto;import com.example.api.Response.ApiResponse;import com.example.api.ServicesImpl.LikeServiceImplementations;import lombok.RequiredArgsConstructor;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.security.core.context.SecurityContextHolder;import org.springframework.security.core.userdetails.UserDetails;import org.springframework.web.bind.annotation.*;@RestController@RequestMapping("api/v1/like")@RequiredArgsConstructorpublic class LikePostControllers {    private final LikeServiceImplementations likeServiceImplementation;    @PostMapping("/add/{postId}")    public ResponseEntity<ApiResponse> Like(@PathVariable Long postId,                                                  @RequestBody LikeRequestDto like){        UserDetails userDetails = (UserDetails) SecurityContextHolder.getContext().getAuthentication().getPrincipal();        LikeRequestDto likeRequest = likeServiceImplementation.likes(postId,like,userDetails.getUsername());          return ResponseEntity.status(HttpStatus.CREATED).body(                ApiResponse.builder()                        .data(likeRequest)                        .message((likeRequest!=null) ? "SUCCESS" : "ERROR")                        .status(HttpStatus.CREATED)                        .build()        );    }}