package com.example.api.Entity;import jakarta.persistence.*;import lombok.Builder;import lombok.Data;import lombok.RequiredArgsConstructor;import java.util.Date;@Table(name = "comment")@Entity@Data@RequiredArgsConstructorpublic class Comment {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long comment_id;    private String comments;    private Date publishedDate;    @ManyToOne()    @JoinColumn(name = "id")    private User users;    @ManyToOne()    @JoinColumn(name = "post_id")    private Post post;}