package com.example.api.Entity;import jakarta.persistence.*;import lombok.Data;import java.util.Date;@Table(name = "comment")@Entity@Datapublic class Comment {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    Long comment_id;    private String comments;    private Date publishedDate;    @ManyToOne()    @JoinColumn(name = "id")    private User users;}