package com.example.api.Entity;import jakarta.persistence.*;import lombok.*;import java.util.Date;@Table(name = "comments")@Entity@Data@AllArgsConstructor@NoArgsConstructor@Builderpublic class Comments {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long comment_id;    private String comments;    private Date publishedDate;    @ManyToOne()    @JoinColumn(name = "id")    private User users;    @ManyToOne()    @JoinColumn(name = "post_id")    private Post post;}