package com.example.api.Entity;import jakarta.persistence.*;import lombok.*;@Entity@Data@NoArgsConstructor@AllArgsConstructor@Builderpublic class Likes {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long like_id;    private Boolean likes;    @ManyToOne    @JoinColumn(name = "id",referencedColumnName = "id")    private User user;    @ManyToOne    @JoinColumn(name = "post_id")    private Post post;}