package com.example.api.Entity;import jakarta.persistence.*;import lombok.Data;import lombok.RequiredArgsConstructor;import java.util.Date;import java.util.List;@Data@Entity@RequiredArgsConstructor@Table(name = "post")public class Post {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    Long post_id;    private String title;    private String content;    private Date publishedDate;    private String author;    private boolean published;    @ManyToOne()    private User user;    @OneToMany    private List<Comment> comment;}