package com.example.api.Entity;import jakarta.persistence.*;import lombok.Builder;import lombok.Data;import lombok.NoArgsConstructor;import lombok.RequiredArgsConstructor;import java.time.LocalDateTime;import java.util.Date;import java.util.List;@Data@Entity@Table(name = "post")@RequiredArgsConstructorpublic class Post {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    Long post_id;    private String title;    private String content;    private LocalDateTime publishedDate;    private String author;    private boolean published;    @ManyToOne()    private User user;    @OneToMany    private List<Comment> comment;}