package com.example.api.Exceptions;import lombok.Getter;import lombok.NoArgsConstructor;import org.aspectj.weaver.tools.Trace;import org.springframework.http.HttpStatus;@NoArgsConstructor@Getterpublic class EmailAddressAlreadyExistException extends RuntimeException {    protected String message;    protected HttpStatus status;    protected Trace trace ;    public EmailAddressAlreadyExistException(String message1) {        this.message = String.format("User with email %s already exist", message1);        this.trace = getTrace();    }}