package com.example.api.Repository;import com.example.api.Entity.Likes;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Query;import org.springframework.stereotype.Repository;import java.util.Optional;@Repositorypublic interface LikeRepositories extends JpaRepository<Likes,Long> {    @Query(value = "SELECT * FROM likes WHERE post_id=?1 and id=?2",nativeQuery = true)    Optional<Likes> findLikesByUserIdAndPost(Long post_id, Long userid);}