package com.example.api.ServicesImpl;import com.example.api.DTO.CommentDTO;import com.example.api.DTO.UserDTO;import com.example.api.Entity.Comment;import com.example.api.Entity.Post;import com.example.api.Entity.User;import com.example.api.Repository.CommentRepository;import com.example.api.Services.CommentService;import jakarta.servlet.http.HttpServletRequest;import jakarta.servlet.http.HttpSession;import lombok.RequiredArgsConstructor;import org.modelmapper.ModelMapper;import org.springframework.stereotype.Service;@Service@RequiredArgsConstructorpublic class CommentServiceImplementation implements CommentService {    private final CommentRepository commentRepository;    private final HttpServletRequest request;    private final ModelMapper modelMapper;    @Override    public CommentDTO commentCreated(CommentDTO commentDTO, Long id, HttpSession session) {        User userDTO = (User) session.getAttribute("userDTO");        Post post  = new Post();        post.setPost_id(id);        commentDTO.setPost(post);        Comment comment = new Comment();                comment.setUsers(userDTO);                comment.setPost(commentDTO.getPost());                comment.setComments(commentDTO.getComments());        CommentDTO savedCommentDTO = modelMapper.map(commentRepository.save(comment), CommentDTO.class);        return savedCommentDTO;    }    @Override    public Comment deleteComment(Long id) {        return null;    }}